package tools;

public class VariableDessin implements ElementDessin {

	private int x;
	
	private int y;

	public VariableDessin(int x, int y) {
		this.x = x;
		this.y = y;
	}

	@Override
	public int getX() {
		// TODO Auto-generated method stub
		return this.x;
	}

	@Override
	public int getY() {
		// TODO Auto-generated method stub
		return this.y;
	}

}
